import React from 'react';
import { useCallback } from 'react';
import { useState } from 'react';
import { useEffect } from 'react';
import { containerStyles } from 'react';
import {
    BrowserRouter as Router,
    Switch,
    Route,
    Link
  } from "react-router-dom"; 
  import { withResizeDetector } from 'react-resize-detector';
  import { useResizeDetector } from 'react-resize-detector';
import ReactDOM from 'react-dom';
import './index.css';
import './image-gallery.css';
import ReactPlayer from "react-player";
// import './Gallery.js';
import ImageGallery from 'react-image-gallery';

//import ImageGallery from 'src/ImageGallery';

const PREFIX_URL = 'https://raw.githubusercontent.com/xiaolin/react-image-gallery/master/static/';



import reportWebVitals from './reportWebVitals';
import { findByLabelText } from '@testing-library/dom';

var mobi = false;


var MOBILETHRESHHOLD = 700;

var services = [ 
  ["Generic", "Service desc"],
  ["Generic #2", "Another service desc"],
  ["Generic #3", "Another service desc"],
  ["Generic #4", "Another service desc"] ,
  ["Generic #5", "Another service desc"] ,
  ["Generic #6", "Service desc"],
  ["Generic #7", "Another service desc"] ,
  ["Generic #8", "Another service desc"],
  ["Generic #9", "Another service desc"] ,
  ["Generic #10", "Another service desc"] ,
  ["Generic #11", "Another service desc"] ,
  ["Generic #12", "Another service desc"] 
]

var QNAs = [
  ["Question? ", "Answer."],
  ["Question? ", "Answer."],
  ["Question? ", "Answer."],
  ["Question? ", "Answer."],
  ["Question? ", "Answer."],
  ["Question? ", "Answer."],
  ["Question? ", "Answer."],
  ["Question? ", "Answer."],
  ["Question? ", "Answer."],
  ["Question? ", "Answer."],
  ["Question? ", "Answer."],
  ["Question? ", "Answer."]
]

const images = [
  {
    original: 'http://lorempixel.com/1000/600/nature/1/',
    thumbnail: 'http://lorempixel.com/250/150/nature/1/',
    
  },
  {
    original: 'http://lorempixel.com/1000/600/nature/2/',
    thumbnail: 'http://lorempixel.com/250/150/nature/2/'
  },
  {
    original: 'http://lorempixel.com/1000/600/nature/3/',
    thumbnail: 'http://lorempixel.com/250/150/nature/3/'
  }
]

const AdaptiveComponent = ({ width, height }) => {
  const [color, setColor] = useState('red');

  useEffect(() => {
    setColor(width > 500 ? 'red' : 'blue');
  }, [width]);


  return (
    <Router>
    <div className = "Router">
    <nav >
    <ul className="RouterList">
        <li className="RouterElm">
        <Link to="/">Home</Link>
        </li>
        <li className="RouterElm">
        <Link to="/about">About</Link>
        </li>
        <li className="RouterElm">
        <Link to="/services">Services</Link>
        </li>
        <li className="RouterElm">
        <Link to="/gallery">Gallery</Link>
        </li>
        <li className="RouterElm">
        <Link to="/faqs">FAQs</Link>
        </li>
        <li className="RouterElm">
        <Link to="/contact">Contact</Link>
        </li>
    </ul>
    </nav>

    {/* A <Switch> looks through its children <Route>s and
        renders the first one that matches the current URL. */}
        <Switch>
        <Route path="/about">
            <About  x={width} y={height}/>
        </Route>
        <Route path="/services">
            <Services x={width} y={height} />
        </Route>
        <Route path="/gallery">
            <Gallery x={width} y={height} />
        </Route>
        <Route path="/FAQs">
            <FAQs x={width} y={height} />
        </Route>
        <Route path="/contact">
            <Contact x={width} y={height} />
        </Route>
        <Route path="/">
            <Home x={width} y={height} />
        </Route>
        </Switch>
    </div>
    < Footer x={width} y={height} />
</Router>);
};

const AdaptiveWithDetector = withResizeDetector(AdaptiveComponent);


/*
CustomComponent();
*/
// HEADER
ReactDOM.render(
  <React.StrictMode>
  <AdaptiveWithDetector />    
  </React.StrictMode>,
  document.getElementById('header')
);

function Home(props) {

  if (props.x < MOBILETHRESHHOLD) {
      return (
        <div className="home-mobile">
          <h1>CD Entertainment</h1>
        </div>
        
      );
  } else {
    return (
      <div className="home-desktop">
        <h1>CD Entertainment</h1>
      </div>
      
    );
  }    
}

function About(props) {
  console.log("Width: " , props.x)
  if (props.x < MOBILETHRESHHOLD) {
   
      return (
        <div className="about-mobile">
        <h1>About Us</h1>
        <img src="about.png" alt="OWNERS"/>
        <div className="about-mobile-desc">
         <p>Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.  </p>
         <p> Ut enim ad minim veniam, quis nostrud exercitation ullamco laboris nisi ut aliquip ex ea commodo consequat.  </p>
         <p> Duis aute irure dolor in reprehenderit in voluptate velit esse cillum dolore eu fugiat nulla pariatur.  </p>
         <p> Excepteur sint occaecat cupidatat non proident, sunt in culpa qui officia deserunt mollit anim id est laborum. </p>
        
        </div>
       </div>
      );
  } else {
      return (
        <div className="about-desktop">
         <h1>About Us</h1>
         <img src="about.png" alt="OWNERS"/>
         <div className="about-desktop-desc">
          <p>Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.  </p>
          <p> Ut enim ad minim veniam, quis nostrud exercitation ullamco laboris nisi ut aliquip ex ea commodo consequat.  </p>
          <p> Duis aute irure dolor in reprehenderit in voluptate velit esse cillum dolore eu fugiat nulla pariatur.  </p>
          <p> Excepteur sint occaecat cupidatat non proident, sunt in culpa qui officia deserunt mollit anim id est laborum. </p>
         
         </div>
        </div>
      );
  }    
}

function Services(props) {
  
  if (props.x < MOBILETHRESHHOLD) {
    var renderedOutput = services.map(item =>  <li className = "ServicesListElm"> <h2> {item[0]} </h2> <p>  {item[1]} </p> </li>);
    return (
        <div className = "services-mobile">
         <h2>Services Mobile</h2>
          { renderedOutput }
        </div>
      );
  } else {
    var renderedOutput = services.map(item =>  <li className = "ServicesListElm"> <h2> {item[0]} </h2> <p>  {item[1]} </p> </li>);
      return (
        <div className = "services-desktop">
          <h1> Services </h1>
          <ul className = "ServicesList">
            { renderedOutput }
          </ul>
        </div>
      );
  }    
}

function Gallery(props) {
  if (props.x < MOBILETHRESHHOLD) {
      return (
        <h1>hello world</h1>
      );
  } else {
      return (
        <div className="gallery-desktop">
          <h1> Gallery </h1>
        <GalleryViewer />
        </div>
        
        );
  }    
}

function FAQs(props) {
  if (props.x < MOBILETHRESHHOLD) {
    var renderedOutput = QNAs.map(item =>  <li className = "QNAsListElm"> <h2> {item[0]} </h2> <p>  {item[1]} </p> </li>);
      return (
        <div className="FAQsMobile">
        <h1>FAQs Desktop</h1>
        { renderedOutput }
        </div>
      )
  } else {
    var renderedOutput = QNAs.map(item =>  <li className = "QNAsListElm"> <h2> {item[0]} </h2> <p>  {item[1]} </p> </li>);
      return (
        <div className="FAQsDesktop">
          <h1>FAQs Desktop</h1>
          <div className="QNAsDesktop">{ renderedOutput }</div>
        </div>
      )
  }    
}

function Contact(props) {
  if (props.x < MOBILETHRESHHOLD) {
    return (
      <div className = "contact-mobile">
        <h1>Contact Us</h1>
        <form className="contact-form">
        <label for="fname">Name</label>
        <input type="text" id= "form-name" value="John"></input> <br></br>
        <label for="fname">Email</label>
        <input type="text" id= "form-email" value="JohnDoe@gmail.com"></input><br></br>
        <label for="fname">Phone Number</label>
        <input type="text" id= "form-number" value="(XXX) XXX-XXXX"></input><br></br>
        <label for="Message">Message</label>
        <input type="textarea" id= "form-message" value="John"></input><br></br>
          <input type="submit" value="submit"></input>
      </form>
      </div>
     
    )
  } else {
      return (
        <div className = "contact-desktop">
        <h1>Contact Us</h1>
        <form className="contact-form">
        <label for="fname">Name</label>
        <input type="text" id= "form-name" value="John"></input> <br></br>
        <label for="fname">Email</label>
        <input type="text" id= "form-email" value="JohnDoe@gmail.com"></input><br></br>
        <label for="fname">Phone Number</label>
        <input type="text" id= "form-number" value="(XXX) XXX-XXXX"></input><br></br>
        <label for="Message">Message</label>
        <textarea id= "form-message" value="message ..."></textarea><br></br>
          <input type="submit" value="submit"></input>
      </form>
      </div>
      )
  }    
}

function Footer(props) {
  if (props.x < MOBILETHRESHHOLD) {
      return (
        <div className = "bannerMobile">
        <ul className="BannerListMobile">
        <li className="BannerElm"><BannerElement label="Instagram :" content="@CD_Entertainment" iconSource="https://cdn-icons-png.flaticon.com/512/174/174855.png" iconAlt="IG" /></li>
        <li className="BannerElm"><BannerElement label="Phone :" content="(XXX) XXX-XXXX" iconSource="phone.png" iconAlt="PHONE" /></li>
        <li className="BannerElm"><BannerElement label="Email :" content="hello@cdentertainment.com" iconSource="email.png" iconAlt="EMAIL" /></li>
        </ul>
      </div>
      )
  } else {
      return (
        <div className = "bannerDesktop">
          <ul className="BannerListDesktop">
          <li className="BannerElm"><BannerElement label="Instagram :" content="@CD_Entertainment" iconSource="./insta.png" iconAlt="IG" /></li>
          <li className="BannerElm"><BannerElement label="Phone :" content="(XXX) XXX-XXXX" iconSource="phone.png" iconAlt="PHONE" /></li>
          <li className="BannerElm"><BannerElement label="Email :" content="hello@cdentertainment.com" iconSource="email.png" iconAlt="EMAIL" /></li>
          </ul>
        </div>
        
      );
  }    
}

function GalleryViewer (props) {
  
  class App extends React.Component {
  
    constructor() {
      super();
      this.state = {
        showIndex: false,
        showBullets: true,
        infinite: true,
        showThumbnails: true,
        showFullscreenButton: true,
        showGalleryFullscreenButton: true,
        showPlayButton: true,
        showGalleryPlayButton: true,
        showNav: true,
        isRTL: false,
        slideDuration: 450,
        slideInterval: 2000,
        slideOnThumbnailOver: false,
        thumbnailPosition: 'bottom',
        showVideo: {},
        useWindowKeyDown: true,
      };
  
      this.images = [
        {
          thumbnail: `${PREFIX_URL}4v.jpg`,
          original: `${PREFIX_URL}4v.jpg`,
          embedUrl: 'https://www.youtube.com/embed/4pSzhZ76GdM?autoplay=1&showinfo=0',
          description: 'Render custom slides (such as videos)',
          renderItem: this._renderVideo.bind(this)
        },
        {
          original: `${PREFIX_URL}1.jpg`,
          thumbnail: `${PREFIX_URL}1t.jpg`,
          originalClass: 'featured-slide',
          thumbnailClass: 'featured-thumb',
          description: 'Custom class for slides & thumbnails',
        },
      ].concat(this._getStaticImages());
    }
  
    _onImageClick(event) {
      console.debug('clicked on image', event.target, 'at index', this._imageGallery.getCurrentIndex());
    }
  
    _onImageLoad(event) {
      console.debug('loaded image', event.target.src);
    }
  
    _onSlide(index) {
      this._resetVideo();
      console.debug('slid to index', index);
    }
  
    _onPause(index) {
      console.debug('paused on index', index);
    }
  
    _onScreenChange(fullScreenElement) {
      console.debug('isFullScreen?', !!fullScreenElement);
    }
  
    _onPlay(index) {
      console.debug('playing from index', index);
    }
  
    _handleInputChange(state, event) {
      if (event.target.value > 0) {
        this.setState({[state]: event.target.value});
      }
    }
  
    _handleCheckboxChange(state, event) {
      this.setState({[state]: event.target.checked});
    }
  
    _handleThumbnailPositionChange(event) {
      this.setState({thumbnailPosition: event.target.value});
    }
  
    _getStaticImages() {
      let images = [];
      for (let i = 2; i < 12; i++) {
        images.push({
          original: `${PREFIX_URL}${i}.jpg`,
          thumbnail:`${PREFIX_URL}${i}t.jpg`
        });
      }
  
      return images;
    }
  
    _resetVideo() {
      this.setState({showVideo: {}});
  
      if (this.state.showPlayButton) {
        this.setState({showGalleryPlayButton: true});
      }
  
      if (this.state.showFullscreenButton) {
        this.setState({showGalleryFullscreenButton: true});
      }
    }
  
    _toggleShowVideo(url) {
      this.state.showVideo[url] = !Boolean(this.state.showVideo[url]);
      this.setState({
        showVideo: this.state.showVideo
      });
  
      if (this.state.showVideo[url]) {
        if (this.state.showPlayButton) {
          this.setState({showGalleryPlayButton: false});
        }
  
        if (this.state.showFullscreenButton) {
          this.setState({showGalleryFullscreenButton: false});
        }
      }
    }
  
    _renderVideo(item) {
      return (
        <div>
          {
            this.state.showVideo[item.embedUrl] ?
              <div className='video-wrapper'>
                  <a
                    className='close-video'
                    onClick={this._toggleShowVideo.bind(this, item.embedUrl)}
                  >
                  </a>
                  <iframe
                    width='560'
                    height='315'
                    src={item.embedUrl}
                    frameBorder='0'
                    allowFullScreen
                  >
                  </iframe>
              </div>
            :
              <a onClick={this._toggleShowVideo.bind(this, item.embedUrl)}>
                <div className='play-button'></div>
                <img className='image-gallery-image' src={item.original} />
                {
                  item.description &&
                    <span
                      className='image-gallery-description'
                      style={{right: '0', left: 'initial'}}
                    >
                      {item.description}
                    </span>
                }
              </a>
          }
        </div>
      );
    }
  
    render() {
      return (
  
        <section className='app'>
          <ImageGallery
            ref={i => this._imageGallery = i}
            items={this.images}
            onClick={this._onImageClick.bind(this)}
            onImageLoad={this._onImageLoad}
            onSlide={this._onSlide.bind(this)}
            onPause={this._onPause.bind(this)}
            onScreenChange={this._onScreenChange.bind(this)}
            onPlay={this._onPlay.bind(this)}
            infinite={this.state.infinite}
            showBullets={this.state.showBullets}
            showFullscreenButton={this.state.showFullscreenButton && this.state.showGalleryFullscreenButton}
            showPlayButton={this.state.showPlayButton && this.state.showGalleryPlayButton}
            showThumbnails={this.state.showThumbnails}
            showIndex={this.state.showIndex}
            showNav={this.state.showNav}
            isRTL={this.state.isRTL}
            thumbnailPosition={this.state.thumbnailPosition}
            slideDuration={parseInt(this.state.slideDuration)}
            slideInterval={parseInt(this.state.slideInterval)}
            slideOnThumbnailOver={this.state.slideOnThumbnailOver}
            additionalClass="app-image-gallery"
            useWindowKeyDown={this.state.useWindowKeyDown}
          />
  
          <div className='app-sandbox'>
  
            <div className='app-sandbox-content'>
              <h2 className='app-header'>Settings</h2>
  
              <ul className='app-buttons'>
                <li>
                  <div className='app-interval-input-group'>
                    <span className='app-interval-label'>Play Interval</span>
                    <input
                      className='app-interval-input'
                      type='text'
                      onChange={this._handleInputChange.bind(this, 'slideInterval')}
                      value={this.state.slideInterval}/>
                  </div>
                </li>
  
                <li>
                  <div className='app-interval-input-group'>
                    <span className='app-interval-label'>Slide Duration</span>
                    <input
                      className='app-interval-input'
                      type='text'
                      onChange={this._handleInputChange.bind(this, 'slideDuration')}
                      value={this.state.slideDuration}/>
                  </div>
                </li>
  
                <li>
                  <div className='app-interval-input-group'>
                    <span className='app-interval-label'>Thumbnail Bar Position</span>
                    <select
                      className='app-interval-input'
                      value={this.state.thumbnailPosition}
                      onChange={this._handleThumbnailPositionChange.bind(this)}
                    >
                      <option value='bottom'>Bottom</option>
                      <option value='top'>Top</option>
                      <option value='left'>Left</option>
                      <option value='right'>Right</option>
                    </select>
                  </div>
                </li>
              </ul>
  
              <ul className='app-checkboxes'>
                <li>
                  <input
                    id='infinite'
                    type='checkbox'
                    onChange={this._handleCheckboxChange.bind(this, 'infinite')}
                    checked={this.state.infinite}/>
                    <label htmlFor='infinite'>allow infinite sliding</label>
                </li>
                <li>
                  <input
                    id='show_fullscreen'
                    type='checkbox'
                    onChange={this._handleCheckboxChange.bind(this, 'showFullscreenButton')}
                    checked={this.state.showFullscreenButton}/>
                    <label htmlFor='show_fullscreen'>show fullscreen button</label>
                </li>
                <li>
                  <input
                    id='show_playbutton'
                    type='checkbox'
                    onChange={this._handleCheckboxChange.bind(this, 'showPlayButton')}
                    checked={this.state.showPlayButton}/>
                    <label htmlFor='show_playbutton'>show play button</label>
                </li>
                <li>
                  <input
                    id='show_bullets'
                    type='checkbox'
                    onChange={this._handleCheckboxChange.bind(this, 'showBullets')}
                    checked={this.state.showBullets}/>
                    <label htmlFor='show_bullets'>show bullets</label>
                </li>
                <li>
                  <input
                    id='show_thumbnails'
                    type='checkbox'
                    onChange={this._handleCheckboxChange.bind(this, 'showThumbnails')}
                    checked={this.state.showThumbnails}/>
                    <label htmlFor='show_thumbnails'>show thumbnails</label>
                </li>
                <li>
                  <input
                    id='show_navigation'
                    type='checkbox'
                    onChange={this._handleCheckboxChange.bind(this, 'showNav')}
                    checked={this.state.showNav}/>
                    <label htmlFor='show_navigation'>show navigation</label>
                </li>
                <li>
                  <input
                    id='show_index'
                    type='checkbox'
                    onChange={this._handleCheckboxChange.bind(this, 'showIndex')}
                    checked={this.state.showIndex}/>
                    <label htmlFor='show_index'>show index</label>
                </li>
                <li>
                  <input
                    id='is_rtl'
                    type='checkbox'
                    onChange={this._handleCheckboxChange.bind(this, 'isRTL')}
                    checked={this.state.isRTL}/>
                    <label htmlFor='is_rtl'>is right to left</label>
                </li>
                <li>
                  <input
                    id='slide_on_thumbnail_hover'
                    type='checkbox'
                    onChange={this._handleCheckboxChange.bind(this, 'slideOnThumbnailOver')}
                    checked={this.state.slideOnThumbnailOver}/>
                    <label htmlFor='slide_on_thumbnail_hover'>slide on mouse over thumbnails</label>
                </li>
                <li>
                  <input
                    id='use_window_keydown'
                    type='checkbox'
                    onChange={this._handleCheckboxChange.bind(this, 'useWindowKeyDown')}
                    checked={this.state.useWindowKeyDown}/>
                    <label htmlFor='use_window_keydown'>use window keydown</label>
                </li>
              </ul>
            </div>
  
          </div>
        </section>
      );
    }
  }
  
  ReactDOM.render(<App/>, document.getElementById('root'));
  
  
}

function BannerElement (props) {
    
    return (
      <ul className="BannerElmList">
        <li className="BannerElmListSublist">
          <img src={props.content} alt={props.iconAlt}></img>
        </li>
        <li className="BannerElmListSublist">
          <p>{props.label} </p>
        </li>
        <li className="BannerElmListSublist">
          <p> {props.content}</p>
        </li>
      </ul>
    )
}



